import "./App.css";
import { useEffect, useState } from "react";
import Axios from "axios";
import Coin from "./components/Coin";

function App() {
  const [listOfCoins, setListOfCoins] = useState([]);
  useEffect(() => {
    Axios.get("https://api.coinstats.app/public/v1/coins?skip=0").then(
      (response) => {
        setListOfCoins(response.data.coins);
      }
    );
  }, []);

  const options = {
    method: 'GET',
    url: 'https://investing-cryptocurrency-markets.p.rapidapi.com/coins/get-overview',
    params: {pair_ID: '33', time_utc_offset: '28800', lang_ID: '1'},
    headers: {
      'X-RapidAPI-Key': 'baefebc7b8mshcd5fb3b528e0861p140df4jsn0f0d16aa1a0b',
      'X-RapidAPI-Host': 'investing-cryptocurrency-markets.p.rapidapi.com'
    }
  };

  Axios.request(options).then(function (response) {
    console.log(response.data);
  }).catch(function (error) {
    console.error(error);
  });

  const [Search, setSearch] = useState("")

  const filteredCoins = listOfCoins.filter((coin) => {
    return coin.name.toLowerCase().includes(Search.toLowerCase())
  })

  return (
    <div className="App">
      <div className="cryptoHeader">
        <input type="text" placeholder="Bitcoin..." onChange={(e) => {
          setSearch(e.target.value);
        }}/>
      </div>
      <div className="cryptoDisplay">
        {filteredCoins.map((coin) => {
          return (
            <Coin
              name={coin.name}
              icon={coin.icon}
              price={coin.price}
              symbol={coin.symbol}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
