import React, { useState } from "react";

function Coin({ name, icon, price, symbol, rank, priceChange1d }) {
  const [isRed, setIsRed] = useState(false);
  const chech1d = () => { 
    if (priceChange1d.includes("-")) {
      setIsRed = true;
    }
  }


  return (
    <div className="coin">
      <h3 className="rank">#{rank}</h3>
      <img src={icon} />
      <h1>{name}</h1>
      <h3>{symbol}</h3>
      <h3>{price.toFixed(2)}</h3>
      <h3 id={isRed ? 'red' : 'green'}>{priceChange1d}</h3>
    </div>
  );
}

export default Coin;
